let htmlItem = document.querySelector('html');
let deviceWidth = window.innerWidth;
let desktopWidth = 1620;
let mobileWidth = 430;

// Обработчик при загрузке
let onDOMLoading = function () {
  checkDeviceWidth();
};

// Получает размер rem для desktop
let getDesktopRootSize = function () {
  let rootSize = deviceWidth / desktopWidth;
  return rootSize;
};

// Получает размер rem для mobile
let getMobileRootSize = function () {
  let rootSize = deviceWidth / mobileWidth;
  return rootSize;
};

// Устанавливает размер rem
let setRootSize = function (rootFontSize) {
  htmlItem.style.fontSize = rootFontSize + 'px';
};

// Проверка ширины устройства
let checkDeviceWidth = function () {
    switch (true){
      case deviceWidth >= 830 && deviceWidth < 1920:
      setRootSize(getDesktopRootSize() - 0.1);
      break;
      case deviceWidth >= 1920:
      setRootSize(1);
      break;
      default:
      setRootSize(getMobileRootSize());
      break;
  }
};
// Событие при загрузке
document.addEventListener('DOMContentLoaded', onDOMLoading);

