document.getElementById('bars').onclick = function () {
  document.getElementById('bars').classList.add('bars_none');
  document.getElementById('close').classList.add('bars_2');
  document.getElementById('flex_nav').classList.add('flex_nav_100');
  document.getElementById('phone').classList.add('main--main-bg');
  document.getElementById('menu').classList.add('main--main-bg');
  document.getElementById('soc').classList.add('main--main-bg');
};
document.getElementById('close').onclick = function () {
  document.getElementById('bars').classList.remove('bars_none');
  document.getElementById('close').classList.remove('bars_2');
  document.getElementById('flex_nav').classList.remove('flex_nav_100');
  document.getElementById('phone').classList.remove('main--main-bg');
  document.getElementById('menu').classList.remove('main--main-bg');
  document.getElementById('soc').classList.remove('main--main-bg');
};